exports.checkSum = checkSum;

var MOD_ADLER = 65521;

function checkSum(buffer, startPos, endPos){
	if(startPos < 0){
		startPos = 0;
	}
	
	if(endPos <= 0){
		endPos += buffer.length;
	}
	
	return encode(buffer, startPos, endPos);
}
		
function encode(buffer, startPos, endPos){
	var a = 1;
	var b = 0;
	
	for(var i=startPos; i<endPos; i++)
	{
		a = (a + buffer[i]) % MOD_ADLER;
		b = (b + a) % MOD_ADLER;
	}
	
	return (b << 16) | a;
}