"use strict";

module.exports = Packet;

var request = "<policy-file-request/>";

var response = '<?xml version="1.0"?>';
response += '<cross-domain-policy>';
response += '<allow-access-from domain="*" to-ports="*"/>';
response += '</cross-domain-policy>';

function Packet(){}

var prototype = Packet.prototype;

prototype.create = function(){
	return new Packet();
}

prototype.getHeadSize = function(){
	return request.length;
}

prototype.getBodySize = function(){
	return 0;
}

prototype.readHead = function(buffer){}
prototype.readBody = function(buffer){}

prototype.toBytes = function(){
	return response;
}